package JavaIntro;

public class IfElse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int mark=70;
		if(mark>=90) {
			System.out.println("Grade A");
		}else if(mark>=80){
			System.out.println("Grade B");
			
		}else if (mark>=70) {
			System.out.println("Grade C");
		}
		else {
			System.out.println("You failed in examination");
			
		}

	}

}
