package JavaIntro;

public class StarPattern {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        int row=5;
        for(int column=1;column<=row;column++) {
        	for(int j=1;j<=column;j++) {
        		System.out.print("* ");
        	}
        	System.out.println();
        	
        }
	}

}
